import { css, unsafeCSS } from 'lit';

// A base64-encoded WOFF2 font file. Contains a single icon with codepoint "e900" and ligature "figma".
const iconFontBase64 =
  '';

export const iconFontCss = css`
  @font-face {
    font-family: 'My icons';
    src: url(data:font/woff2;charset=utf-8;base64,${unsafeCSS(iconFontBase64)}) format('woff2');
  }

  .custom-line-height {
    line-height: 1.5;
  }

  .icon-before::before {
    font-family: 'My icons';
    content: '\\e900';
  }

  .icon-after::after {
    font-family: 'My icons';
    content: '\\e900';
  }

  .icon-ligature::before {
    font-family: 'My icons';
    content: 'figma';
  }
`;
