import { fixtureSync } from '@vaadin/testing-helpers/dist/fixture.js';
import { visualDiff } from '@web/test-runner-visual-regression';
import '../../../theme/lumo/vaadin-message.js';

describe('message', () => {
  let div, element;

  ['ltr', 'rtl'].forEach((dir) => {
    describe(dir, () => {
      before(() => {
        document.documentElement.setAttribute('dir', dir);
      });

      after(() => {
        document.documentElement.removeAttribute('dir');
      });

      describe('basic', () => {
        beforeEach(() => {
          div = document.createElement('div');
          div.style.padding = '10px';
          element = fixtureSync(
            `<vaadin-message>There is no real ending. It's just the place where you stop the story.</vaadin-message>`,
            div
          );
        });

        it('basic', async () => {
          await visualDiff(div, `${import.meta.url}_${dir}-basic`);
        });

        it('time', async () => {
          element.time = '2021-01-28 10:43';
          await visualDiff(div, `${import.meta.url}_${dir}-time`);
        });

        it('userName', async () => {
          element.userName = 'Bob Ross';
          await visualDiff(div, `${import.meta.url}_${dir}-user-name`);
        });

        it('userName time', async () => {
          element.userName = 'Bob Ross';
          element.time = 'Moments ago';
          await visualDiff(div, `${import.meta.url}_${dir}-user-name-time`);
        });

        it('userAbbr', async () => {
          element.userAbbr = 'AF';
          await visualDiff(div, `${import.meta.url}_${dir}-user-abbr`);
        });

        it('userImg', async () => {
          /* prettier-ignore */
          element.userImg = 'data:image/svg+xml;base64,';
          await visualDiff(div, `${import.meta.url}_${dir}-user-img`);
        });

        it('userColorIndex', async () => {
          element.userColorIndex = 3;
          await visualDiff(div, `${import.meta.url}_${dir}-user-color-index`);
        });
      });
    });
  });
});
